    public function fromArray($arr, $keyType = BasePeer::TYPE_PHPNAME)
    {
        $keys = PromocionPeer::getFieldNames($keyType);

        if (array_key_exists($keys[0], $arr)) $this->setId($arr[$keys[0]]);
        if (array_key_exists($keys[1], $arr)) $this->setDescripcion($arr[$keys[1]]);
        if (array_key_exists($keys[2], $arr)) $this->setFechaInicio($arr[$keys[2]]);
        if (array_key_exists($keys[3], $arr)) $this->setFechaFin($arr[$keys[3]]);
        if (array_key_exists($keys[4], $arr)) $this->setEstado($arr[$keys[4]]);
        if (array_key_exists($keys[5], $arr)) $this->setProductoId($arr[$keys[5]]);
        if (array_key_exists($keys[6], $arr)) $this->setDescuento($arr[$keys[6]]);
        if (array_key_exists($keys[7], $arr)) $this->setPromocionGlobal($arr[$keys[7]]);
