    /**
     * Build a Criteria object containing the values of all modified columns in this object.
     *
     * @return Criteria The Criteria object containing all modified values.
     */
    public function buildCriteria()
    {
        $criteria = new Criteria(PromocionPeer::DATABASE_NAME);

        if ($this->isColumnModified(PromocionPeer::ID)) $criteria->add(PromocionPeer::ID, $this->id);
        if ($this->isColumnModified(PromocionPeer::DESCRIPCION)) $criteria->add(PromocionPeer::DESCRIPCION, $this->descripcion);
        if ($this->isColumnModified(PromocionPeer::FECHA_INICIO)) $criteria->add(PromocionPeer::FECHA_INICIO, $this->fecha_inicio);
        if ($this->isColumnModified(PromocionPeer::FECHA_FIN)) $criteria->add(PromocionPeer::FECHA_FIN, $this->fecha_fin);
        if ($this->isColumnModified(PromocionPeer::ESTADO)) $criteria->add(PromocionPeer::ESTADO, $this->estado);
        if ($this->isColumnModified(PromocionPeer::PRODUCTO_ID)) $criteria->add(PromocionPeer::PRODUCTO_ID, $this->producto_id);
        if ($this->isColumnModified(PromocionPeer::DESCUENTO)) $criteria->add(PromocionPeer::DESCUENTO, $this->descuento);
        if ($this->isColumnModified(PromocionPeer::PROMOCION_GLOBAL)) $criteria->add(PromocionPeer::PROMOCION_GLOBAL, $this->promocion_global);

        return $criteria;
    }

    /**
