        $copyObj->setFechaInicio($this->getFechaInicio());
        $copyObj->setFechaFin($this->getFechaFin());
        $copyObj->setEstado($this->getEstado());
        $copyObj->setProductoId($this->getProductoId());
        $copyObj->setDescuento($this->getDescuento());
        $copyObj->setPromocionGlobal($this->getPromocionGlobal());

        if ($deepCopy && !$this->startCopy) {
            // important: temporarily setNew(false) because this affects the behavior of
            // the getter/setter methods for fkey referrer objects.
            $copyObj->setNew(false);
            // store object hash to prevent cycle
            $this->startCopy = true;

            //unflag object copy
            $this->startCopy = false;
        } // if ($deepCopy)

        if ($makeNew) {
            $copyObj->setNew(true);
            $copyObj->setId(NULL); // this is a auto-increment column, so set to default value
        }
    }

