            $retval = null;

            $failureMap = array();


            // We call the validate method on the following object(s) if they
            // were passed to this object by their coresponding set
            // method.  This object relates to these object(s) by a
            // foreign key reference.

            if ($this->aCliente !== null) {
                if (!$this->aCliente->validate($columns)) {
                    $failureMap = array_merge($failureMap, $this->aCliente->getValidationFailures());
                }
            }

            if ($this->aUsuario !== null) {
                if (!$this->aUsuario->validate($columns)) {
                    $failureMap = array_merge($failureMap, $this->aUsuario->getValidationFailures());
                }
            }

            if ($this->aTipoTransaccion !== null) {
                if (!$this->aTipoTransaccion->validate($columns)) {
                    $failureMap = array_merge($failureMap, $this->aTipoTransaccion->getValidationFailures());
                }
            }

            if ($this->aTipoPago !== null) {
                if (!$this->aTipoPago->validate($columns)) {
                    $failureMap = array_merge($failureMap, $this->aTipoPago->getValidationFailures());
                }
            }


            if (($retval = TransaccionPeer::doValidate($this, $columns)) !== true) {
                $failureMap = array_merge($failureMap, $retval);
            }



            $this->alreadyInValidation = false;
        }

        return (!empty($failureMap) ? $failureMap : true);
